let bibtexDict = Object();
for (let i = 0; i < bibtex.length; i++) {
  let item = bibtex[i];
  bibtexDict[item.citationKey] = item;
}

Array.prototype.forEach.call(citations, function (citation) {


  // console.log(citation)

  const refIds = citation.getAttribute("citation-ref").split(',');
  let citationText = '';
  let citationNumbers = [];
  let citationRedable = [];
  for (let i = 0; i < refIds.length; i++) {
    const refId = refIds[i];
    const refIndexInRefs = Object.keys(bibtexDict).indexOf(refId);
    if (refIndexInRefs === -1) {
      citationNumbers.push('??');
    } else {
      const index = uniqueReferences.indexOf(refId);
      if (index === -1) {
        uniqueReferences.push(refId);
      }
      const citationNumber = uniqueReferences.indexOf(refId) + 1;
      citationNumbers.push(citationNumber.toString());
      citationText = citationText + '<div>' + '[' + citationNumber + ']' + getRef(refId) + '</div>'

      // Create readable ref, e.g. [Dopierre et al., 2021]

      citationData = bibtexDict[refId].entryTags;
      out = '';
      if (citationData.authors != null) {
        author =  citationData.authors.split(',')[0]
        if (author.length < citationData.authors.length) {
          author += ' et al.'
        }
        out += author
      } else if (citationData.author != null) {
        author = citationData.author.split(',')[0]
        if (author.length < citationData.author.length) {
          author += ' et al.'
        }
        out += author
      }
      if (citationData.years != null) {
        out += ', ' + citationData.years
      } else if (citationData.year != null) {
        out += ', ' + citationData.year
      }
      citationRedable.push(out)

    }
  }


  // citation.innerHTML = "[" + citationNumber + "] "+'<span class="tooltiptext bottom formula"> mytooltip</span>';
  citation.innerHTML = '<span class="number formula tooltip top"><span class="text-blue-500">&nbsp;[' + citationRedable.join(', ') + ']</span><span class="tooltiptext top formula">' + citationText + '</span></span>'
  // console.log(refId);
})


function getRef(refId) {
  const el = bibtexDict[refId].entryTags;
  // console.log('el', el)

  let out = ''
  if (el.authors != null) {
    out += ' ' + el.authors + '.';
  } else if (el.author != null) {
    out += ' ' + el.author + '.';
  }
  if (el.titles != null) {
    out += ' <em class="text-blue-500">' + el.titles + '.</em>';
  } else if (el.title != null) {
    out += ' <em class="text-blue-500">' + el.title + '.</em>';
  }
  if (el.booktitles != null) {
    out += ' ' + el.booktitles + '.';
  } else if (el.booktitle != null) {
    out += ' ' + el.booktitle + '.';
  }
  if (el.journals != null) {
    out += ' ' + el.journals + '.';
  }
  if (el.years != null) {
    out += ' <span class="text-red-100">' + el.years + '</span>.';
  } else if (el.year != null) {
    out += ' <span class="text-red-100">' + el.year + '</span>.';
  }
  return out;

}

